import React, { useState } from "react";
import "./style.css";

const App = (props) => {
	const [newItem, setInput] = useState("");

	const handleChange = (e) => {
		setInput(e.target.value);
	};

	return (
		<div className="todo_div">
			<h1> ToDo List</h1>
			<input
				type="text"
				name="username"
				id="txt_username"
				placeholder="Enter Your Todo"
				className="txt_username"
			/>
			<br />
			<input
				type="submit"
				name="add todo"
				id="add_todo"
				value="add"
				className="button"
				onclick={handleChange}
			/>
		</div>
	);
};

export default App;
