import React, { useState } from "react";
import "./style.css";

const App = () => {
	const [items, setitems] = useState("");
	const [elements, setElements] = useState([]);

	const onchangesDone = (event) => {
		setitems(event.target.value);
	};

	const listOfItems = () => {
		setElements((oldItems) => {
			return [...oldItems, items];
		});
		setitems("");
	};

	const deleteitems = (index) => {
		console.log("deleted", index);
	};

	return (
		<div className="todo_div">
			<h1> Todo</h1>
			<input
				type="text"
				name="username"
				id="txt_username"
				placeholder="Enter Your Todo"
				className="txt_username"
				value={items}
				onChange={onchangesDone}
			/>
			<br />
			<input
				type="button"
				name="add todo"
				id="add_todo"
				value="add"
				className="button"
				onClick={listOfItems}
			/>

			<div className="elements">
				<ol>
					{elements.map((element, index) => {
						return (
							<div
								style={{
									display: "flex",
									flexDirection: "row",
									justifyContent: "center",
									alignItems: "center",
								}}
								key={index}
							>
								<i
									className="fa fa-times"
									aria-hidden="true"
									id="fatime"
									onClick={deleteitems(index)}
									style={{
										color: "crimson",
										paddingRight: "20px",
										fontSize: "30px",
									}}
								/>
								<li
									style={{
										listStyle: "none",
										fontSize: "30px",
										fontFamily: "monospace",
										color: "black",
									}}
								>
									{element}
								</li>
							</div>
						);
					})}
				</ol>
			</div>
		</div>
	);
};

export default App;
