import React, { useState } from "react";
import "./style.css";

const App = (props) => {
	const [newItem, setInput] = useState("");

	const handleChange = (e) => {
		setInput(e.target.value);
	};

	const handleSubmit = (e) => {
		e.preventDefault();

		props.onclick({
			id: Math.floor(Math.random() * 10000),
			text: newItem,
		});

		setInput("");
	};

	return (
		<div className="todo_div">
			<h1> ToDo List</h1>
				type="text"
			<input
				name="username"
				id="txt_username"
				placeholder="Enter Your Todo"
				className="txt_username"
				value={newItem}
				onChange={handleChange}
			/>
			<br />
			<input
				type="submit"
				name="add todo"
				id="add_todo"
				value="add"
				className="button"
				onclick={handleSubmit}
			/>
		</div>
	);
};

export default App;
