import React, { useState } from "react";
import "./style.css";
import TodoList from "./TodoList";

const getLocalData = () => {
	let list = localStorage.getItem("lists");
	if (list) {
		return JSON.parse(localStorage.getItem("lists"));
	} else {
		return [];
	}
};

const App = () => {
	const [items, setitems] = useState("");
	const [elements, setElements] = useState(getLocalData());

	const onchangesDone = (event) => {
		setitems(event.target.value);
	};

	const listOfItems = () => {
		setElements((oldItems) => {
			return [...oldItems, items];
		});
		localStorage.setItem("lists", JSON.stringify(elements));
		setitems("");
	};

	const deleteitems = (id) => {
		setElements((oldItems) => {
			return oldItems.filter((value, index) => {
				localStorage.removeItem(value);
				return index !== id;
			});
		});
	};

	return (
		<div className="todo_div">
			<h1> Todo</h1>
			<input
				type="text"
				name="username"
				id="txt_username"
				placeholder="Enter Your Todo"
				className="txt_username"
				value={items}
				onChange={onchangesDone}
			/>
			<br />
			<input
				type="button"
				name="add todo"
				id="add_todo"
				value="add"
				className="button"
				onClick={listOfItems}
			/>

			<div className="elements">
				<ol>
					{elements.map((element, index) => {
						return (
							<TodoList
								key={index}
								id={index}
								text={element}
								onSelect={deleteitems}
							/>
						);
					})}
				</ol>
			</div>
		</div>
	);
};

export default App;
