import React, { useState } from "react";
import "./style.css";

const App = () => {
	const [items, setitems] = useState("");
	const [elements, setElements] = useState([]);

	const onchangesDone = (event) => {
		setitems(event.target.value);
	};

	const listOfItems = () => {
		setElements((oldItems) => {
			return [...oldItems, items];
		});
		setitems("");
	};

	return (
		<div className="todo_div">
			<h1> Todo</h1>
			<input
				type="text"
				name="username"
				id="txt_username"
				placeholder="Enter Your Todo"
				className="txt_username"
				value={items}
				onChange={onchangesDone}
			/>
			<br />
			<input
				type="submit"
				name="add todo"
				id="add_todo"
				value="add"
				className="button"
				onClick={listOfItems}
			/>

			<div className="elements">
				<ol>
					{elements.map((element) => {
						return (
							<div
								style={{
									display: "flex",
									marginLeft: "595px",
								}}
							>
								<i
									class="fa fa-times"
									aria-hidden="true"
									id="fatime"
									style={{ paddingRight: "20px" }}
								></i>
								<li
									style={{
										listStyle: "none",
										fontSize: "15em",
									}}
								>
									{element}
								</li>
							</div>
						);
					})}
				</ol>
			</div>
		</div>
	);
};

export default App;
